<?php get_header(); ?>

<?php
  $slug = pods_v( 'last', 'url' );
  $pods = pods('artikel', $slug);

  //variable
  $title = $pods->field('name'); // Title
  $content = $pods->field('content'); // Content
  $intro = $pods->field('intro.name'); // Intro
  $auteur = $pods->field('auteur.name'); // Auteur
  $datum = $pods->field('datum.name');
  $auteur_bio = $pods->field('auteur_bio.name'); // Auteur
  $illustratie = $pods->field('illustratie.name'); // Illustratue auteur
  $featured_img = pods_image_url(
    $pods->field('featured_img'),
    'thumbnail',
    0,
    true
  );
  $jaargang_nummer = $pods->field('jaargang_nummer.name');
  $permalink = site_url('artikel/' . $pods->field('permalink'));
  $url_encode = urlencode($permalink);

  if($featured_img != ''){
    $img = '<div class="image"><img src="'.$featured_img.'" alt="'.$title.'"></div>';
  } else {
    $img = '';
  }
?>


<div class="body">
    <!-- START IMAGE -->
    <?php echo $img; ?>
    <!-- END IMAGE -->

    <!-- START CONTENT -->
    <div class="content artikel">
        <h1><?php echo $title; ?></h1> <!-- FIRST HEADER -->
        <small><?php echo $auteur; ?>, <?php echo date("d-m-Y", strtotime($datum)); ?><?php if($jaargang_nummer != '0.00' && $jaargang_nummer != ''){ echo ', JAARGANG NR. ' . $jaargang_nummer;} ?></small>
        <p class="intro"> <!-- TEXT -->
            <?php echo $intro; ?>
        </p>

        <?php echo wpautop($content);?>

        <!-- BIO TEXT -->

        <p class="bio">
          <?php echo $auteur_bio; ?>
        </p>
        <br>
        <!-- CONTENT FOOTER -->

        <div class="foot">
            <h2>Deel dit bericht</h2>
            <div class="icons">
                <a href="https://twitter.com/home?status=<?php echo $url_encode; ?>" target="_blank"><img src="data:image/png;base64,"></a>
                <a href="https://www.facebook.com/sharer/sharer.php?u=<?php echo $url_encode; ?>" target="_blank"><img src="data:image/png;base64,"></a>
